import React, {useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { 
  faCircleCheck, faL, faPen, faTrash, faTrashCan
 } from '@fortawesome/free-solid-svg-icons'

import './App.css';

function App() {

  // Tasks (ToDolist) state

  const[toDo, setToDo] = useState([
    {"id":1, "title":"Task 1", "Satus":false},
    {"id":2, "title":"Task 2", "Satus":false}
  ]);


  // temp State

  const[newTask, setnewTask] = useState('');
  const[updateData, setupdateData] = useState('');


    //Add task

    const addTask = () => {
      if(newTask) {
          let num = toDo.length +1
          let newEntry = {id:num, title:newTask, status:false}
          setToDo([...toDo, newEntry])
          setnewTask('');
      }

    }

    // Delete Task

    const deleteTask = (id) => {
        let newTasks = toDo.filter( task => task.id !== id)
        setToDo(newTasks);
    }

    // Mark Task as done or completed

    const markDone = (id) => {
      const newTask = toDo.map( task => {
        if (task.id === id ) {
          return ({...task, status:!task.status})
        }
        return task;
      })
      setToDo(newTask)

    }

    // Cancel Update

    const cancelUpdate = () => {
      setupdateData('');

    }

    // Change task for update

    const changeTask = (e) => {
      let newEntry = {
        id: updateData.id,
        title: e.target.value,
        status: updateData.status ? true : false
      }
      setupdateData(newEntry);

    }

    // Update Task

    const UpdateTask = () => {
      let filterRecords = [...toDo].filter

    }

  return (
    <div className="container App">
      <br /><br/>
      <h2>To Do List App(ReactJS)</h2>
      <br /><br/>

      {/* Add Task */}

      <div className='row'>
        <div className='col'>
          <input 
          value={newTask}
          onChange={(e)=> setnewTask(e.target.value)}
          className='form-control form-control-lg'/>
        </div>
        <div className='col-auto'>
          <button 
          onClick={addTask}
          className='btn btn-lg btn-success'>Add Task</button>
        </div>
      </div>
      <br/>

      {/* Update Task */}
      <div className='row'>
        <div className='col'>
          <input className='form-control form-control-lg' value={updateData && updateData.title} onChange={(e)=> changeTask()}/>
        </div>
        <div className='col-auto'>
          <button className='btn btn-lg btn-success mr-20' onClick={UpdateTask}>Update</button>
          <button className='btn btn-lg btn-warning'>Cancel</button>
        </div>
      </div>
      <br/>




      {/* Dispaly Todos */}

      {toDo && toDo.length ? '':'No Tasks...'}


      {toDo && toDo
        .sort((a, b)=>a.id > b.id ? 1:-1)
        .map((task, index) => {
          return(
            <React.Fragment key={task.id}>


              <div className='col taskBg'>

                <div className={task.status? 'done': ''}>
                  <span className='taskNumber'>{index + 1}</span>
                  <span className='taskText'>{task.title}</span>
                </div>

                <div className="iconsWrap">

                  <span onClick={ (e) => markDone(task.id)} title='Completed/Not Completed'>
                      <FontAwesomeIcon icon={faCircleCheck}/> 
                  </span>


                  {task.status ? null : (
                    <span title='Edit' onClick={() => setupdateData({id:task.id, title:task.title, status:task.status ? true : false})}>
                    <FontAwesomeIcon icon={faPen}/>
                  </span>
                  )}


                  <span title='Delete' onClick={()=> deleteTask(task.id)}>
                    <FontAwesomeIcon icon={faTrashCan}/>
                  </span>

                </div>

              </div>


            </React.Fragment>
          )
        })
      }


    </div>
  );
}

export default App;
