import React, {useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faCircleCheck,faPen,faTrashCan} from '@fortawesome/free-solid-svg-icons'


const ToDo = ({toDo, markDone, setUpdateData, deleteTask,setShow})=>{

  const handleClickDelete = (id) => {
    // setShow(true)
    // console.log(id); 
  }
  
    return(
        <>
        
            {toDo && toDo
                .sort((a, b)=>a.id > b.id ? 1:-1)
                .map((task, index) => {
                return(
                <React.Fragment key={task.id}>

              <div className='col taskBg'>

                <div className={task.status? 'done': ''}>
                  <span className='taskNumber'>{index + 1}</span>
                  <span className='taskText'>{task.title}</span>
                </div>
 
                <div className="iconsWrap">

                  <span onClick={ (e) => markDone(task.id)} title='Completed/Not Completed'>
                      <FontAwesomeIcon icon={faCircleCheck}/> 
                  </span>

                  {task.status ? null : (
                    <span title='Edit' onClick={() => setUpdateData({id: task.id, title:task.title, status:task.status ? true : false})}>
                    <FontAwesomeIcon icon={faPen}/>
                  </span>
                  )}

                  {/* <span title='Delete' onClick={()=> handleClickDelete(task.id)}> */}
                  <span title='Delete' onClick={()=> setShow(true)}>
                    <FontAwesomeIcon icon={faTrashCan}/>
                  </span>
                  
                </div>
              </div> 
            </React.Fragment>
          )


          })
      }
        </>
    )}

export default ToDo;
