import React, {useState} from 'react'

import AddTaskForm from './components/AddTaskForm.jsx';
import UpdateForm from './components/UpdateForm.jsx';
import ToDo from './components/ToDo.jsx';


import 'bootstrap/dist/css/bootstrap.min.css'


import './App.css';

function App() {

  // Tasks (ToDolist) state

  const[toDo, setToDo] = useState([]);


  // temp State

  const[newTask, setNewTask] = useState('');
  const[updateData, setUpdateData] = useState('');

  const [deleteTask, setDeleteTask] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);





    //Add task

    const addTask = () => {
      if(newTask) {
          let num = toDo.length +1
          let newEntry = {id:num, title:newTask, status:false}
          setToDo([...toDo, newEntry])
          setNewTask('');
      }

    }

    // Delete Task

    // const deleteTask = (id) => {
    //     let newTasks = toDo.filter( task => task.id !== id)
    //     setToDo(newTasks);
    // }

    const handleConfirmDelete = () => {
      // Perform the delete action using the deleteTask variable
      // For example: Call a delete API, update the state, or remove the task from the array

      setDeleteTask(null); // Clear the deleteTask variable
      setShowConfirmation(false); // Close the confirmation modal
    };

    const handleCancelDelete = () => {
      setDeleteTask(null); // Clear the deleteTask variable
      setShowConfirmation(false); // Close the confirmation modal
    };

    {showConfirmation && (
      <div className="confirmation-modal">
        <p>Are you sure you want to delete the task?</p>
        <button onClick={handleConfirmDelete}>Yes</button>
        <button onClick={handleCancelDelete}>No</button>
      </div>
    )}





    // Mark Task as done or completed

    const markDone = (id) => {
      const newTask = toDo.map( task => {
        if (task.id === id ) {
          return ({...task, status:!task.status})
        }
        return task;
      })
      setToDo(newTask)

    }

    // Cancel Update

    const cancelUpdate = () => {
      setUpdateData('');

    }

    // Change task for update

    const changeTask = (e) => {
      let newEntry = {
        id: updateData.id,
        title: e.target.value,
        status: updateData.status ? true : false
      }
      setUpdateData(newEntry);

    }

    // Update Task

    const UpdateTask = () => {
      let filterRecords = [...toDo].filter(task => task.id !==updateData.id);
      let updatedObject = [...filterRecords, updateData]
      setToDo(updatedObject);
      setUpdateData("");

    }

  return (
    <div className="container App">
      <br /><br/>
      <h2>To Do List</h2>
      <br /><br/>

      {updateData && updateData ? (
        <UpdateForm 
          updateData={updateData}
          changeTask={changeTask}
          UpdateTask={UpdateTask}
          cancelUpdate={cancelUpdate}
        />
      ):(
          <AddTaskForm 
            newTask={newTask}
            setNewTask={setNewTask}
            addTask={addTask}
          />
      )}
      {toDo && toDo.length ? '':'No Tasks...'}


      <ToDo 
      toDo={toDo}
      markDone={markDone}
      setUpdateData={setUpdateData}
      // deleteTask={deleteTask}
      />
      </div>
  );
}

export default App;
